async def spammer(app, msg, tries, sleep_time, text):
    await msg.delete()
    try:
        int(tries)
    except:
        float(tries)
    for i in range(int(tries)):
        if (msg.reply_to_message) == None:
            await app.send_message(chat_id=msg.chat.id, text=text)
        else:
            await app.send_message(chat_id=msg.chat.id, text=text, reply_to_message_id=msg.reply_to_message.id)
        try:
            await asyncio.sleep(int(sleep_time))
        except:
            await asyncio.sleep(float(sleep_time))

